def healpix_level_N(source_id, level, constant = False):
    '''returns the HEALpix pixel from Gaia Source ID'''
    if constant == True:
        return 2**35 * 4**(12-level)
    else:    
        return np.floor(source_id/(2**35 * 4**(12-level)))

