%spark.pyspark

def select_training_features(df, regime):
    '''selects correct features for the given regime'''

    features = [i for i in df.columns if i not in ['source_id', 'parallax_over_error', 
                                                   'is_good', 'hpx6', 'm_g', 'g_rp']]

    if 'low' in regime:
        features.remove('abs_SNR')
    elif not 'high' in regime: raise NameError('regime is either "high" or "low"')
    return features

%spark.pyspark

def getScaler(scalerType):
    '''Returns correct scaler for chosen normalization.'''

    if scalerType == 'standard': 
        from pyspark.ml.feature import StandardScaler
        Scaler = StandardScaler(withMean=True, withStd = True)
    elif scalerType == 'MinMax':
        from pyspark.ml.feature import MinMaxScaler
        Scaler = MinMaxScaler()
    else: raise NameError(f'scalerType can be either "MinMax" or "standard" not {scaler}')
    return Scaler


def getScalerModel(df, scalerType = 'standard', featuresCol = 'features',
                       outCol = 'norm_features', save = False):
    '''normalizes DenseVectors for MLlib'''

    scaler = getScaler(scalerType = scalerType) # Get scaler
    scaler.setInputCol(featuresCol)         # Set feature column name
    scaler.setOutputCol(outCol)
    model = scaler.fit(df)
    if save:
        model.save(f'{save}/{scalerType}_normalisation_model')
        print(f'Normalisation model saved at "{save}/{scalerType}_normalisation_model"')
    return model

def normaliseData(df, model,):
    '''normalizes featureCol of df using model from "getScalerModel" '''

    return model.transform(df)

