def loadScaler(filename, scalerType = 'standard'):
    '''loads scaler based of scaler type'''
    if scalerType == 'standard':
        from pyspark.ml.feature import StandardScalerModel
        model = StandardScalerModel.load(filename)

    elif scaterType == 'MinMax':
        from pyspark.ml.feature import MinMaxScalerModel
        model = MinMaxScalerModel.load(filename)
    else: raise NameError(f'scalerType can be either "MinMax" or "standard" not {scalerType}')
    return model

def nnLoadModel(filepath):
